SELECT 
    p.id AS postId,
    p.username,
    p.postedAgo,
    p.content AS postContent,
    p.image,
    p.likeCount,
    c.id AS commentId,
    c.content AS commentContent,
    c.timestamp AS commentTimestamp
FROM 
    posts p
LEFT JOIN 
    comments c ON p.id = c.post_id
ORDER BY 
    p.postedAgo DESC;

